
let text = document.getElementById('text');
let treeLeft = document.getElementById('tree-left');
let treeright = document.getElementById('tree-right');
let gateLeft = document.getElementById('gate-left');
let gateright = document.getElementById('gate-right');

window.addEventListener('scroll', () => {
    let value = window.scrollY;

    text.style.marginTop = value * 0.9 + 'px';
    treeLeft.style.left = value * -1 + 'px';
    treeright.style.left = value * 1 + 'px';
    gateLeft.style.left = value * 0.5 + 'px';
    gateright.style.left = value * -0.5 + 'px';
});
const submitBtn = document.getElementById("emotion_submit");
const textInput = document.getElementById("emotion_inputText");
const speakInput = document.getElementById("emotion_inputSpeak");

const emojiOutput = document.getElementById("emotion_outputEmoji");
const textOutput = document.getElementById("emotion_outputText");

submitBtn.addEventListener("click", (ev) => {
  semantic_analysis_api().then((nthEmoji) => {
    
    currentEmotionTexts = emotionTexts[Number(nthEmoji)];
    const randomIndextext = Math.floor(Math.random() * currentEmotionTexts.length);
    const randomIndexemoji = Math.floor(Math.random() *  moji_listm[nthEmoji].length);
    const dedicated_emoji = moji_listm[nthEmoji][randomIndexemoji];
    emojiOutput.innerHTML = dedicated_emoji;
    textOutput.innerHTML = `<div id="emotion_outputText">${currentEmotionTexts[randomIndextext]}</div>`;
  });
});

const semantic_analysis_api = async () => {
  const url = `https://api.aiforthai.in.th/emoji?text=${encodeURIComponent(
    textInput.value
  )}`;
  const headers = {
    Apikey: "6SdwbWBIe03JeTwlhBLKMM4Xyk3KnxHX",
  };

  const requestData = await fetch(url, {
    method: "GET",
    headers: headers,
  }).then((response) => response.json());

  const nthEmoji = Object.keys(requestData).sort(
    (a, b) => requestData[b] - requestData[a]
  )[0];

  return nthEmoji;
};

speakInput.addEventListener("click", (event) => {
  var recognition = new (window.SpeechRecognition ||
    window.webkitSpeechRecognition ||
    window.mozSpeechRecognition ||
    window.msSpeechRecognition)();

  recognition.lang = "th-TH";
  recognition.interimResults = false;
  recognition.maxAlternatives = 5;

  recognition.start();

  recognition.onresult = async (event) => {
    recognitionResult = await event.results[0][0].transcript;
    textInput.value = recognitionResult;
  };
});

